<?php
 namespace Predis\Command; abstract class ScriptCommand extends ServerEvalSHA { abstract public function getScript(); protected function getKeysCount() { return 0; } public function getKeys() { return array_slice($this->getArguments(), 2, $this->getKeysCount()); } protected function filterArguments(array $arguments) { if (($numkeys = $this->getKeysCount()) && $numkeys < 0) { $numkeys = count($arguments) + $numkeys; } return array_merge(array(sha1($this->getScript()), (int) $numkeys), $arguments); } public function getEvalArguments() { $arguments = $this->getArguments(); $arguments[0] = $this->getScript(); return $arguments; } } 