<?php
 namespace Predis; use Predis\Connection\NodeConnectionInterface; abstract class CommunicationException extends PredisException { private $connection; public function __construct( NodeConnectionInterface $connection, $message = null, $code = null, \Exception $innerException = null ) { parent::__construct($message, $code, $innerException); $this->connection = $connection; } public function getConnection() { return $this->connection; } public function shouldResetConnection() { return true; } public static function handle(CommunicationException $exception) { if ($exception->shouldResetConnection()) { $connection = $exception->getConnection(); if ($connection->isConnected()) { $connection->disconnect(); } } throw $exception; } } 