<?php
 namespace Predis\Response\Iterator; use Predis\Response\ResponseInterface; abstract class MultiBulkIterator implements \Iterator, \Countable, ResponseInterface { protected $current; protected $position; protected $size; public function rewind() { } public function current() { return $this->current; } public function key() { return $this->position; } public function next() { if (++$this->position < $this->size) { $this->current = $this->getValue(); } } public function valid() { return $this->position < $this->size; } public function count() { return $this->size; } public function getPosition() { return $this->position; } abstract protected function getValue(); } 